// function to prepare request for PERSON or PERSON-BASIC API
// IMPORTANT: validToken is NOT required (validToken = null)
function createPersonRequest(url, uinfin, _sp_esvcId, attributes, validToken) {

  var url = url + "/" + uinfin + "/";
  var cacheCtl = "no-cache";
  var method = "GET";

  // assemble params for Person API
  var strParams = "client_id=" + _clientId +
    "&attributes=" + attributes +
    "&sp_esvcId=" + _sp_esvcId;

  var params = querystring.parse(strParams);

  // assemble headers for Person API
  var strHeaders = "Cache-Control=" + cacheCtl;
  var headers = querystring.parse(strHeaders);

  // Add Authorisation headers for connecting to API Gateway
  var authHeaders = securityHelper.generateAuthorizationHeader(
    url,
    params,
    method,
    "", // no content type needed for GET
    _authLevel,
    _clientId,
    _privateKeyContent,
    _clientSecret
  );

  // NOTE: include access token in Authorization header as "Bearer " (with space behind)
  if(!_.isEmpty(validToken)){
    if(!_.isEmpty(authHeaders)){
      authHeaders = authHeaders + ",Bearer " + validToken
    }
    else{
      authHeaders = "Bearer " + validToken
    }
  }

  if (!_.isEmpty(authHeaders)) {
    _.set(headers, "Authorization", authHeaders);
  }

  // invoke person API
  var request = restClient.get(url);

  // Set headers
  if (!_.isUndefined(headers) && !_.isEmpty(headers))
    request.set(headers);

  // Set Params
  if (!_.isUndefined(params) && !_.isEmpty(params))
    request.query(params);

  return request;
}

